  getInverntorydata = ()=> {
    Axios.get('http://localhost:3306/').
    then((response) =>{
      const data = response.data;
      this.setState({ posts: data});
      console.log('Data has been received');

      this.setState({
        products : data
      })
     console.log(this.state.products[0])
    }).catch(() => {
      alert("error")

    });
